class Lesson(db.Model):
    __tablename__ = "lesson"

    id = db.Column(db.Integer, primary_key=True)
    title = db.Column(db.String(120), nullable=False)
    slug = db.Column(db.String(150), nullable=False)

    video_url = db.Column(db.String(255), nullable=True)
    reference_link = db.Column(db.String(500), nullable=True)
    notes = db.Column(db.Text, nullable=True)
    document_url = db.Column(db.String(255), nullable=True)
    duration = db.Column(db.Float, nullable=True)
    size = db.Column(db.Integer, nullable=True)
    created_at = db.Column(db.DateTime, default=datetime.utcnow)

    section_id = db.Column(db.Integer, db.ForeignKey("sections.id"), nullable=False)
    section = db.relationship("Section", back_populates="lessons")
    progress = db.relationship("Progress", back_populates="lesson")

class Quiz(db.Model):
    __tablename__ = "quiz"

    id = db.Column(db.Integer, primary_key=True)
    question = db.Column(db.String(500), nullable=False)
    options = db.Column(db.JSON, nullable=True)  # Only needed for multiple choice
    correct_answer = db.Column(db.String(200), nullable=False)
    quiz_type = db.Column(db.String(50), nullable=False)  # multiple_choice, true_false, free_text
    explanation = db.Column(db.String(1000), nullable=True)

    lesson_id = db.Column(db.Integer, db.ForeignKey("lesson.id"), nullable=False)
    lesson = db.relationship("Lesson", backref=db.backref("quizzes", cascade="all, delete"))                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     